import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import logo from "../"; // replace with your logo image

const Login = () => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    // simple check, replace with API/auth logic later
    if (userId === "admin" && password === "1234") {
      localStorage.setItem("auth", "true"); // save login state
      navigate("/dashboard");
    } else {
      alert("Invalid credentials!");
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white shadow-lg rounded-xl p-8 w-96 text-center">
        <img src={logo} alt="Logo" className="mx-auto mb-6 w-12" />
        <h2 className="text-2xl font-semibold mb-6">Login to Dashboard</h2>
        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="text"
            placeholder="Phone number or User ID"
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            className="w-full border rounded-lg px-3 py-2"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border rounded-lg px-3 py-2"
          />
          <button
            type="submit"
            className="w-full bg-orange-500 hover:bg-orange-600 text-white py-2 rounded-lg"
          >
            Login
          </button>
        </form>
        <div className="mt-4 text-sm">
          <a href="#" className="text-blue-500">
            Forgot password?
          </a>
        </div>
        <div className="mt-6 text-sm">
          Donâ€™t have an account?{" "}
          <a href="/signup" className="text-blue-500 font-medium">
            Signup now!
          </a>
        </div>
      </div>
    </div>
  );
};

export default Login;
