import React, { useState } from "react";

const SignupPage = () => {
  const [phone, setPhone] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!/^\d{10}$/.test(phone)) {
      setError("Enter a valid 10-digit phone number");
      return;
    }
    setError("");
    // Trigger OTP flow here
    console.log("OTP requested for:", phone);
  };

  return (
    <div className="max-w-5xl mx-auto px-4 py-12" style={{marginTop: '150px'}}>

      {/* Hero Section & Signup Form side-by-side container */}
      <section className="flex flex-col md:flex-row md:items-center md:gap-16">
        
        {/* Left Section: Hero image and text */}
        <div className="flex-1 text-center md:text-left">
          <img src="media-images/signup.png" alt="Zerodha dashboard" className="mx-auto" />
        </div>

        {/* Right Section: Signup Form */}
        <div className="flex-1 max-w-lg mx-auto md:mx-0 p-8 space-y-6">
          <h2 className="text-2xl font-semibold">Signup now</h2>
          <p className="text-sm text-gray-500">Or track your existing application</p>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="flex items-center">
              <span className="bg-gray-200 py-2.5 px-3 rounded-l-lg border border-gray-300 border-r-0 text-gray-700 font-medium">+91</span>
              <input
                type="text"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                placeholder="Enter your mobile number"
                className="flex-1 rounded-r-lg border-gray-300 focus:ring-2 focus:ring-blue-600 focus:border-blue-600"
              />
            </div>
            {error && <p className="text-sm text-red-500">{error}</p>}
            <button
              type="submit"
              className="w-full py-2.5 px-4 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition"
            >
              Get OTP
            </button>
          </form>
          <p className="text-xs text-gray-500 mt-4">
            By proceeding, you agree to the Zerodha{" "}
            <a href="/terms" className="underline text-blue-600">
              terms
            </a>{" "}
            &{" "}
            <a href="/privacy-policy" className="underline text-blue-600">
              privacy policy
            </a>
            .
          </p>
          <p className="text-sm text-gray-600 mt-3">
            Looking to open NRI account?{" "}
            <a href="/nri-signup" className="text-blue-600 underline">
              Click here
            </a>
          </p>
        </div>
      </section>

      ---

      {/* Benefits */}
      <section className="space-y-8 mt-16">
        <h2 className="text-2xl font-semibold text-center">
          Investment options with Zerodha demat account
        </h2>
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-4">
          {[
            { title: "Stocks", desc: "Invest in all exchange-listed securities" },
            {
              title: "Mutual Funds",
              desc: "Invest in commission-free direct mutual funds",
            },
            { title: "IPO", desc: "Apply to the latest IPOs instantly via UPI" },
            {
              title: "Futures & Options",
              desc: "Hedge and mitigate market risk with simplified F&O trading",
            },
          ].map((b, idx) => (
            <div
              key={idx}
              className="p-6 bg-white rounded-xl shadow hover:shadow-lg transition"
            >
              <h3 className="text-lg font-semibold mb-2">{b.title}</h3>
              <p className="text-sm text-gray-600">{b.desc}</p>
            </div>
          ))}
        </div>
      </section>

      ---

      {/* Steps */}
      <section className="space-y-8">
        <h2 className="text-2xl font-semibold text-center">
          Steps to open a demat account
        </h2>
        <div className="flex flex-col md:flex-row md:justify-center gap-8">
          {[
            "Enter the requested details",
            "Complete e-sign & verification",
            "Start investing!",
          ].map((step, idx) => (
            <div key={idx} className="flex items-start gap-3">
              <div className="text-blue-600 text-2xl font-bold">
                {String(idx + 1).padStart(2, "0")}
              </div>
              <p className="text-gray-700">{step}</p>
            </div>
          ))}
        </div>
      </section>

      ---

      {/* FAQs */}
      <section className="space-y-8">
        <h2 className="text-2xl font-semibold text-center">FAQs</h2>
        <div className="space-y-6">
          <div>
            <h3 className="font-medium text-gray-900">
              What is a Zerodha account?
            </h3>
            <p className="text-gray-600 text-sm mt-1">
              A Zerodha account is a combined demat and trading account that
              allows investors to buy, sell, and hold securities digitally.
            </p>
          </div>
          <div>
            <h3 className="font-medium text-gray-900">
              What documents are required to open a demat account?
            </h3>
            <ul className="list-disc list-inside text-sm text-gray-600 mt-1 space-y-1">
              <li>PAN number</li>
              <li>Aadhaar linked with mobile number</li>
              <li>Bank statement or cancelled cheque</li>
              <li>Income proof (for F&O trading)</li>
            </ul>
          </div>
          <div>
            <h3 className="font-medium text-gray-900">
              Is Zerodha account opening free?
            </h3>
            <p className="text-gray-600 text-sm mt-1">
              Yes, it is completely free.
            </p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default SignupPage;
