import React from 'react';

function Hero() {
    return ( 
        <div className='pricing-container container mt-5 p-5'>
            <h2 className='text-center'>Charges</h2>
            <p className='text-muted text-center fs-5'>List of all charges and taxes</p>
            <div className='row mt-5'>
                <div className='col-4'>
                    <img src='media-images/pricingEquity.svg' style={{width:"80%"}}></img>
                    <h2 className='text-center mb-4'>Free equity delivery</h2>
                    <p className='text-center text-muted'>All equity delivery investments (NSE, BSE), are absolutely free — ₹ 0 brokerage.</p>
                </div>
                <div className='col-4'></div>
                <div className='col-4'></div>
            </div>
        </div>
     );
}

export default Hero;
